import logo from './logo.svg';
import './App.css';
import ScrollBox from './152-ScrollBox';
import { useRef } from 'react';

const App = () => {
  const scrollBoxRef = useRef();
  return (
    <div>
      <ScrollBox ref={scrollBoxRef} />
      <button onClick={() => scrollBoxRef.scrollToBottom()}>맨 밑으로</button>
    </div>
  );
};

export default App;
